mapper.select:
		select * from t_bookmark_table where 
		(select TourMembercode from umemeber_table a, t_member_table b where a.mcode = b.mcode and a.mid = #{tourmembercode})
    	and contentid = #{contentid}

		select * from t_good_table where 
		(select TourMembercode from umemeber_table a, t_member_table b where a.mcode = b.mcode and a.mid = #{tourmembercode})
    	and contentid = #{contentid}
